mapper.select:
SELECT *
FROM (SELECT ROWNUM RN, A.*
FROM (SELECT * FROM
MVC_BOARD ORDER BY BGROUP DESC, BSTEP) A)
WHERE RN BETWEEN #{startRow}
AND #{endRow}

mapper.update:
		UPDATE MVC_BOARD 
  		SET BNAME = #{bname},
      	BTITLE = #{btitle},
      	BCONTENT = #{bcontent},
      	BIP = #{bip}
  		WHERE BID = #{bid}
